import { ImageResponse } from '@vercel/og'
import { type NextRequest } from 'next/server'

export const config = {
  runtime: 'edge',
}

export default async function handler(
  request: NextRequest
): Promise<ImageResponse> {
  const { searchParams } = new URL(request.url)

  // ?title=<title>
  const title = searchParams.has('title') ? searchParams.get('title') ?? '' : ''
  const university = searchParams.has('university')
    ? searchParams.get('university') ?? ''
    : ''
  const subject = searchParams.has('subject')
    ? searchParams.get('subject') ?? ''
    : ''
  const user = searchParams.has('user') ? searchParams.get('user') ?? '' : ''

  const nunitoSansMedium = await fetch(
    new URL('../../assets/NunitoSans-Medium.ttf', import.meta.url)
  ).then(async res => await res.arrayBuffer())

  const nunitoSansBold = await fetch(
    new URL('../../assets/NunitoSans-Bold.ttf', import.meta.url)
  ).then(async res => await res.arrayBuffer())

  return new ImageResponse(
    (
      <div
        style={{
          display: 'flex',
          flexDirection: 'column',
          fontSize: 56,
          background: 'linear-gradient(to left top, #08213F, #1B406F)',
          lineHeight: 1,
          width: '100%',
          height: '100%',
          padding: '164px 50px',
          color: 'white',
          justifyContent: 'center',
          gap: 40,
        }}
      >
        <span
          style={{
            display: 'block',
            textOverflow: 'ellipsis',
            lineClamp: 2,
            fontWeight: '700',
          }}
        >
          {title}
        </span>
        <div
          style={{
            display: 'flex',
            gap: 10,
            flexDirection: 'column',
          }}
        >
          <span
            style={{
              display: 'flex',
              alignItems: 'center',
              fontWeight: '500',
              fontSize: 36,
            }}
          >
            ğŸ« {university}
          </span>
          <span
            style={{
              display: 'flex',
              alignItems: 'center',
              fontWeight: '500',
              fontSize: 36,
            }}
          >
            ğŸ““ {subject}
          </span>
          <span
            style={{
              display: 'flex',
              alignItems: 'center',
              fontWeight: '500',
              fontSize: 36,
            }}
          >
            ğŸ‘¤ @{user}
          </span>
        </div>
        <Imagotipo />
      </div>
    ),
    {
      width: 1200,
      height: 630,
      fonts: [
        { name: 'nunitoSans', data: nunitoSansMedium, weight: 500 },
        { name: 'nunitoSans', data: nunitoSansBold, weight: 700 },
      ],
    }
  )
}

